import { useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Callback({ onLogin }) {
  const navigate = useNavigate();

  useEffect(() => {
    const code = new URLSearchParams(window.location.search).get('code');
    console.log("ðŸ” Code param:", code);

    const handleOAuthRedirect = async () => {
      if (!code) {
        console.error("Error: No code parameter in URL");
        navigate('/');
        return;
      }

      try {
        const res = await axios.get(`http://localhost:5000/api/oauth2callback?code=${code}`);
        
        if (res.data?.tokens?.access_token) {
          const accessToken = res.data.tokens.access_token;
          console.log('âœ… Access Token received:', accessToken);

          // Save the access token in localStorage
          localStorage.setItem('accessToken', accessToken);

          // Call onLogin callback and navigate to the main page
          onLogin({ accessToken });
          navigate('/');
        } else {
          console.error("Error: No access token received from server");
          localStorage.removeItem('accessToken'); // Clean up in case of failure
          navigate('/'); // Redirect back to login
        }
      } catch (error) {
        console.error("OAuth callback failed:", error);
        localStorage.removeItem('accessToken'); // Clean up invalid token
        navigate('/'); // Redirect back to login
      }
    };

    handleOAuthRedirect();
  }, [navigate]);

  return <p>Logging you in...</p>;
}
