import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Login';
import Callback from './components/Callback';
import EmailList from './components/EmailList';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/api/session', { withCredentials: true })
      .then(res => {
        console.log('‚úÖ Session found:', res.data);
        setUser(res.data);
        setLoading(false);
      })
      .catch(() => {
        console.log('‚ö†Ô∏è No session');
        setUser(null);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>üîÑ Checking session...</p>;

  return (
    <Router>
      <Routes>
        <Route path="/" element={user ? <EmailList /> : <Login />} />
        <Route path="/auth/callback" element={<Callback onLogin={setUser} />} />
      </Routes>
    </Router>
  );
}


export default App;
