import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

export default function Login({ onLogin }) {
  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
    onScriptLoadError={() => console.log("Failed to load Google OAuth script")}
    onScriptLoadSuccess={() => console.log("Google OAuth script loaded")}>
      <div className="login-container">
        <h2>Login with Google</h2>
        <GoogleLogin
          onSuccess={async (credentialResponse) => {
            try {
              // Send the credential to your backend for verification
              const res = await axios.post('http://localhost:5000/api/auth', {
                token: credentialResponse.credential
              });
              onLogin(res.data.accessToken); // Use backend-verified token
            } catch (error) {
              console.error('Auth failed:', error);
            }
          }}
          onError={() => console.log('Login Failed')}
          useOneTap // Optional: enables Google One Tap
        />
      </div>
    </GoogleOAuthProvider>
  );
}
