import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function Callback({ onLogin }) {
  const navigate = useNavigate();

  useEffect(() => {
    const code = new URLSearchParams(window.location.search).get('code');
    console.log("üîç Code param:", code);

    const handleOAuthRedirect = async () => {
      if (!code) {
        console.error("‚ùå Error: No code parameter in URL");
        navigate('/');
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/oauth2callback', {
          params: { code },
          withCredentials: true,
        });

        console.log('‚úÖ OAuth callback response:', res.data);

        if (res.data?.message === 'Login successful') {
          onLogin?.();
          navigate('/');
        } else {
          console.error("‚ùå Unexpected response from server:", res.data);
          navigate('/');
        }
      } catch (error) {
        console.error("‚ùå OAuth callback failed:", error);
        navigate('/');
      }
    };

    handleOAuthRedirect();
  }, [navigate, onLogin]);

  return <p>üîÑ Logging you in...</p>;
}
