import { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import './EmailList.css';

// Helper functions
const cleanBody = (html) => {
  if (!html || typeof html !== 'string') return '';
  return html
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<\/?(html|head|body|meta|title|link)[^>]*>/gi, '')
    .replace(/<div[^>]+class="gmail_quote"[^>]*>[\s\S]*$/gi, '')
    .replace(/<blockquote[^>]*>[\s\S]*?<\/blockquote>/gi, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/\s{2,}/g, ' ')
    .trim();
};

const getDateLabel = (dateStr) => {
  const date = new Date(dateStr);
  const today = new Date();
  const yesterday = new Date();
  yesterday.setDate(today.getDate() - 1);

  if (date.toDateString() === today.toDateString()) return 'Today';
  if (date.toDateString() === yesterday.toDateString()) return 'Yesterday';
  return date.toLocaleDateString(undefined, { year: 'numeric', month: 'short', day: 'numeric', weekday: 'long' });
};

const getInitials = (from) => {
  const name = from.split('<')[0].trim();
  return name.split(' ').map(word => word[0]).join('').slice(0, 2).toUpperCase();
};


export default function EmailList({ token }) {
  const [profile, setProfile] = useState(null);
  const [threads, setThreads] = useState([]);
  const [search, setSearch] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [followEmails, setFollowEmails] = useState([]);
  const [emailInput, setEmailInput] = useState('');
  const [viewMode, setViewMode] = useState('grid');
  
  // Thread-specific state
  const [expandedThreads, setExpandedThreads] = useState({});
  const [threadSearches, setThreadSearches] = useState({});
  const [pendingReplies, setPendingReplies] = useState({});
  const [attachments, setAttachments] = useState({});
  const [editors, setEditors] = useState({});
  const [showPreviews, setShowPreviews] = useState({});

  // Create editor for a thread
  const createEditor = useCallback((threadId) => {
    const editor = useEditor({
      extensions: [StarterKit],
      editorProps: {
        handleKeyDown(view, event) {
          if (event.key === 'Tab') {
            event.preventDefault();
            view.dispatch(view.state.tr.insertText('    '));
            return true;
          }
          return false;
        }
      },
      content: '',
    });
    
    setEditors(prev => ({...prev, [threadId]: editor}));
  }, []);

  // Fetch profile data
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/profile', {
          withCredentials: true,
        });
        setProfile(res.data);
        setUserEmail(res.data.email);
      } catch (err) {
        console.error('❌ Failed to load profile:', err.message);
      }
    };

    fetchProfile();
  }, []);

  // Fetch emails for followed addresses
  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const res = await axios.post('http://localhost:5000/api/emails/followed', {
          senders: followEmails,
        }, {
          headers: {
            'Authorization': `Bearer ${token}`
          },
          withCredentials: true,
        });
        setThreads(res.data);
      } catch (error) {
        console.error('❌ Email fetch failed:', error.response?.data || error.message);
      }
    };

    if (followEmails.length > 0) {
      fetchEmails();
    }
  }, [followEmails, token]);

  // Initialize editors when threads are expanded
  useEffect(() => {
    Object.entries(expandedThreads).forEach(([threadId, isExpanded]) => {
      if (isExpanded && !editors[threadId]) {
        createEditor(threadId);
      }
    });
  }, [expandedThreads, editors, createEditor]);

  // Add email to follow list
  const handleAddEmail = () => {
    if (emailInput && !followEmails.includes(emailInput)) {
      setFollowEmails((prev) => [...prev, emailInput]);
      setEmailInput('');
    }
  };

  // Toggle thread expansion
  const toggleThread = (threadId) => {
    setExpandedThreads(prev => ({
      ...prev,
      [threadId]: !prev[threadId]
    }));
  };

  // Update thread search
  const updateThreadSearch = (threadId, value) => {
    setThreadSearches(prev => ({
      ...prev,
      [threadId]: value
    }));
  };

  // Add/remove attachments
  const updateAttachments = (threadId, newAttachments) => {
    setAttachments(prev => ({
      ...prev,
      [threadId]: newAttachments
    }));
  };

  // Set pending reply metadata
  const setPendingReplyMeta = (threadId, meta) => {
    setPendingReplies(prev => ({
      ...prev,
      [threadId]: meta
    }));
    setShowPreviews(prev => ({
      ...prev,
      [threadId]: true
    }));
  };

  // Toggle preview visibility
  const togglePreview = (threadId, value) => {
    setShowPreviews(prev => ({
      ...prev,
      [threadId]: value
    }));
  };

  // Send or save reply
  const sendReply = async (threadId, to, subject, isDraft) => {
    const editor = editors[threadId];
    if (!editor) return;
    
    const html = editor.getHTML();
    
    try {
      await axios.post('http://localhost:5000/api/reply', {
        threadId,
        to,
        subject,
        message: html,
        draft: isDraft
      }, { withCredentials: true });

      // Add reply to local thread
      setThreads(prev =>
        prev.map(thread => {
          if (thread.id !== threadId) return thread;
          return {
            ...thread,
            messages: [
              ...thread.messages,
              {
                id: `local-${Date.now()}`,
                from: userEmail,
                subject,
                date: new Date().toISOString(),
                body: html,
              }
            ],
          };
        })
      );

      // Show success message
      alert(isDraft ? '✅ Draft saved!' : '✅ Reply sent!');

      // Reset editor and state
      editor.commands.setContent('');
      
      // Reset preview state
      togglePreview(threadId, false);
      
      // Clear attachments
      updateAttachments(threadId, []);
      
    } catch (err) {
      console.error('❌ Reply failed:', err);
      alert('Failed to send reply.');
    }
  };

  // Filter threads based on search
  const filteredThreads = threads.filter(thread =>
    thread.subject?.toLowerCase().includes(search.toLowerCase()) ||
    thread.messages?.some(msg =>
      msg.from?.toLowerCase().includes(search.toLowerCase()) ||
      msg.body?.toLowerCase().includes(search.toLowerCase())
    )
  );

  return (
    <div style={styles.container} className="email-list">
      {/* Profile Section */}
      {profile && (
        <div style={styles.profileContainer}>
          <img src={profile.picture} alt="avatar" style={styles.profileImage} />
          <div>
            <div><strong>{profile.name}</strong></div>
            <div style={styles.profileEmail}>{profile.email}</div>
          </div>
        </div>
      )}

      {/* Search and Email Management */}
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="🔍 Search all threads..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={styles.search}
        />

        <div style={styles.emailInputContainer}>
          <input
            type="text"
            placeholder="Add email to follow..."
            value={emailInput}
            onChange={(e) => setEmailInput(e.target.value)}
            style={styles.emailInput}
          />
          <button
            onClick={handleAddEmail}
            style={styles.addButton}
          >
            Add Email
          </button>
        </div>

        <div style={styles.followHeader}>
          <div style={styles.followCount}>
            {followEmails.length > 0 ? 
              `Following ${followEmails.length} email${followEmails.length > 1 ? 's' : ''}` : 
              'No emails followed yet'}
          </div>
          <div style={styles.viewButtons}>
            <button 
              onClick={() => setViewMode('grid')}
              style={styles.viewButton(viewMode === 'grid')}
            >
              Grid View
            </button>
            <button 
              onClick={() => setViewMode('list')}
              style={styles.viewButton(viewMode === 'list')}
            >
              List View
            </button>
          </div>
        </div>

        <div style={styles.emailChips}>
          {followEmails.map((email, index) => (
            <div key={index} style={styles.emailChip}>
              <span>{email}</span>
              <button 
                onClick={() => setFollowEmails(prev => prev.filter((_, i) => i !== index))}
                style={styles.removeButton}
              >
                ×
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Email Threads */}
      {filteredThreads.length > 0 ? (
        <div style={viewMode === 'grid' ? styles.gridContainer : {}}>
          {filteredThreads.map((thread) => {
            const isExpanded = expandedThreads[thread.id] || false;
            const threadSearch = threadSearches[thread.id] || '';
            const threadAttachments = attachments[thread.id] || [];
            const editor = editors[thread.id];
            
            const filteredMsgs = thread.messages.filter(msg =>
              msg.from.toLowerCase().includes(threadSearch.toLowerCase()) ||
              msg.body.toLowerCase().includes(threadSearch.toLowerCase())
            );

            let lastDateLabel = '';

            return (
              <div key={thread.id} className={`email-thread ${isExpanded ? 'expanded' : ''}`} 
                  style={styles.threadContainer(isExpanded)}>
                <div style={styles.threadHeader} onClick={() => toggleThread(thread.id)}>
                  <h3 style={styles.threadTitle}>
                    📬 {thread.subject || '(no subject)'}
                  </h3>
                  <span style={styles.threadToggle(isExpanded)}>▼</span>
                </div>
                
                <div style={styles.threadInfo}>
                  {thread.messages.length} message{thread.messages.length !== 1 ? 's' : ''} • 
                  Last activity: {new Date(thread.messages[thread.messages.length - 1].date).toLocaleString()}
                </div>

                {isExpanded && (
                  <div style={{ marginTop: '15px' }}>
                    <input
                      type="text"
                      placeholder="Search in this thread..."
                      value={threadSearch}
                      onChange={(e) => updateThreadSearch(thread.id, e.target.value)}
                      style={styles.threadSearch}
                    />

                    <div style={styles.messagesContainer}>
                      {filteredMsgs.map((msg, i) => {
                        const dateLabel = getDateLabel(msg.date);
                        const showDateLabel = dateLabel !== lastDateLabel;
                        lastDateLabel = dateLabel;

                        const isMe = msg.from.includes(userEmail);

                        return (
                          <div key={i} style={{ display: 'flex', flexDirection: 'column' }}>
                            {showDateLabel && (
                              <div style={styles.dateLabel}>
                                ── {dateLabel} ──
                              </div>
                            )}
                            <div className={`message ${isMe ? 'sent' : 'received'}`} style={styles.message(isMe)}>
                              <div className="avatar" style={styles.avatar(isMe)}>
                                {isMe ? 'You' : getInitials(msg.from)}
                              </div>
                              <div style={styles.messageContent}>
                                <div style={styles.messageSender}>{isMe ? 'You' : msg.from}</div>
                                <div className="email-body" style={styles.messageBody}
                                  dangerouslySetInnerHTML={{ __html: cleanBody(msg.body) }} />
                                <div style={styles.messageTime}>{new Date(msg.date).toLocaleString()}</div>
                              </div>
                            </div>
                          </div>
                        );
                      })}
                    </div>

                    <div style={styles.replyContainer}>
                      <h4 style={{ margin: '0 0 10px 0' }}>Reply</h4>
                      {editor && (
                        <div style={styles.toolbarContainer}>
                          <button
                            onClick={() => editor.chain().focus().toggleBold().run()}
                            className={editor.isActive('bold') ? 'active' : ''}
                            title="Bold"
                            aria-label="Bold"
                            style={styles.toolbarButton(editor.isActive('bold'))}
                          >
                            B
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().toggleItalic().run()} 
                            style={styles.toolbarButton(editor.isActive('italic'))}
                          >
                            I
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().toggleStrike().run()} 
                            style={styles.toolbarButton(editor.isActive('strike'))}
                          >
                            S
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().setParagraph().run()}
                            style={styles.toolbarButton(editor.isActive('paragraph'))}
                          >
                            P
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()}
                            style={styles.toolbarButton(editor.isActive('heading', { level: 1 }))}
                          >
                            H1
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().toggleHeading({ level: 2 }).run()}
                            style={styles.toolbarButton(editor.isActive('heading', { level: 2 }))}
                          >
                            H2
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().toggleBulletList().run()}
                            style={styles.toolbarButton(editor.isActive('bulletList'))}
                          >
                            • List
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().toggleOrderedList().run()}
                            style={styles.toolbarButton(editor.isActive('orderedList'))}
                          >
                            1. List
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().undo().run()}
                            style={styles.toolbarButton(false)}
                          >
                            ↺ Undo
                          </button>
                          <button 
                            onClick={() => editor.chain().focus().redo().run()}
                            style={styles.toolbarButton(false)}
                          >
                            ↻ Redo
                          </button>
                        </div>
                      )}

                      {editor && (
                        <EditorContent 
                          editor={editor} 
                          className="tiptap-editor"
                          style={styles.editorContainer}
                        />
                      )}

                      <div style={styles.attachmentSection}>
                        <label style={styles.attachmentLabel}>📎 Attach files:
                          <input 
                            type="file" 
                            multiple
                            onChange={(e) => {
                              const newFiles = Array.from(e.target.files || []);
                              updateAttachments(thread.id, [...threadAttachments, ...newFiles]);
                            }} 
                            style={{ display: 'block', marginTop: '6px' }} 
                          />
                        </label>
                        <div style={styles.attachmentList}>
                          {threadAttachments.map((file, index) => (
                            <div key={index} style={styles.attachmentItem}>
                              <span style={{ marginRight: '5px' }}>📄</span>
                              {file.name}
                              <button 
                                onClick={() => updateAttachments(thread.id, threadAttachments.filter((_, i) => i !== index))} 
                                style={styles.removeButton}
                              >
                                ×
                              </button>
                            </div>
                          ))}
                        </div>
                      </div>
                      
                      <div style={styles.buttonGroup}>
                        <button
                          onClick={() => sendReply(thread.id, thread.messages[0].from, thread.subject, false)}
                          style={styles.actionButton('#4caf50')}
                        >
                          <span style={{ fontSize: '1.2em' }}>✉️</span>
                          Send Reply
                        </button>
                        <button
                          onClick={() => {
                            setPendingReplyMeta(thread.id, {
                              threadId: thread.id,
                              to: thread.messages[0].from,
                              subject: thread.subject,
                            });
                          }}
                          style={styles.actionButton('#2196f3')}
                        >
                          <span style={{ fontSize: '1.2em' }}>👁️</span>
                          Preview
                        </button>
                        <button
                          onClick={() => sendReply(thread.id, thread.messages[0].from, thread.subject, true)}
                          style={styles.actionButton('#ff9800')}
                        >
                          <span style={{ fontSize: '1.2em' }}>💾</span>
                          Save Draft
                        </button>
                      </div>
                    </div>
                  </div>
                )}

                {/* Preview Modal */}
                {showPreviews[thread.id] && pendingReplies[thread.id] && (
                  <div style={styles.previewModal}>
                    <div style={styles.previewCard}>
                      <h3 style={styles.previewTitle}>📨 Preview Your Reply</h3>
                      <div style={styles.previewMeta}>
                        <div style={styles.previewMetaItem}><strong>To:</strong> {pendingReplies[thread.id].to}</div>
                        <div style={styles.previewMetaItem}><strong>Subject:</strong> {pendingReplies[thread.id].subject}</div>
                      </div>
                      <div style={styles.previewContent} 
                        dangerouslySetInnerHTML={{ __html: editor ? editor.getHTML() : '' }} />
                      
                      {threadAttachments.length > 0 && (
                        <div style={{ marginTop: '15px' }}>
                          <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>Attachments:</div>
                          <div style={styles.attachmentList}>
                            {threadAttachments.map((file, index) => (
                              <div key={index} style={styles.attachmentItem}>
                                <span role="img" aria-label="file">📄</span> {file.name}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                      
                      <div style={{ marginTop: '15px', display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                      <button 
                        onClick={() => togglePreview(thread.id, false)} 
                        style={{ 
                          padding: '8px 16px', 
                          backgroundColor: '#e0e0e0', 
                          border: 'none', 
                          borderRadius: '6px', 
                          cursor: 'pointer' 
                        }}
                      >
                        Cancel
                      </button>
                      <button 
                        onClick={() => {
                          sendReply(pendingReplies[thread.id].threadId, pendingReplies[thread.id].to, pendingReplies[thread.id].subject, false);
                          togglePreview(thread.id, false);
                        }} 
                        style={{ 
                          padding: '8px 16px', 
                          backgroundColor: '#4caf50', 
                          color: 'white', 
                          border: 'none', 
                          borderRadius: '6px', 
                          cursor: 'pointer' 
                        }}
                      >
                        Send
                      </button>
                    </div>
                  </div>
                  </div>
                  )}
                  </div>
                  );
                  })}
                  </div>
                  ) : (
                  <div style={{ 
                    textAlign: 'center', 
                    padding: '40px', 
                    backgroundColor: '#f5f5f5', 
                    borderRadius: '10px',
                    border: '1px dashed #ccc'
                  }}>
                    {followEmails.length > 0 ? (
                      <p>📨 Loading emails...</p>
                    ) : (
                      <p>📭 No emails to show. Add an email address to follow above.</p>
                    )}
                  </div>
                  )}
                  </div>
                  );
                  }
