import { useEffect, useState } from 'react';
import axios from 'axios';
import './EmailList.css';

export default function EmailList({ token }) {
  const [threads, setThreads] = useState([]);
  const [expandedThreadId, setExpandedThreadId] = useState(null);
  const [search, setSearch] = useState('');
  const [visibleCount, setVisibleCount] = useState(5);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/emails', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setThreads(res.data);
      } catch (error) {
        console.error('Email fetch failed:', error.response?.data || error.message);
      }
    };

    if (token) fetchEmails();
  }, [token]);

  const toggleThread = (id) => {
    setExpandedThreadId(expandedThreadId === id ? null : id);
  };

  const getInitials = (from) => {
    const name = from.split('<')[0].trim();
    return name.split(' ').map(word => word[0]).join('').slice(0, 2).toUpperCase();
  };

  const formatDateHeader = (dateStr) => {
    const date = new Date(dateStr);
    const today = new Date();
    const yesterday = new Date();
    yesterday.setDate(today.getDate() - 1);

    if (date.toDateString() === today.toDateString()) return 'Today';
    if (date.toDateString() === yesterday.toDateString()) return 'Yesterday';

    return date.toLocaleDateString(undefined, {
      weekday: 'short', year: 'numeric', month: 'short', day: 'numeric'
    });
  };

  const filteredThreads = threads
    .filter((thread) => thread.subject?.toLowerCase().includes(search.toLowerCase()) ||
      thread.messages?.some(msg => msg.from?.toLowerCase().includes(search.toLowerCase()) ||
        msg.body?.toLowerCase().includes(search.toLowerCase())))
    .slice(0, visibleCount);

  return (
    <div className="email-list">
      <input
        type="text"
        placeholder="ðŸ” Search emails..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="search-bar"
      />

      {filteredThreads.length > 0 ? (
        filteredThreads.map((thread) => (
          <div key={thread.id} className={`email-thread ${expandedThreadId === thread.id ? 'expanded' : ''}`}>
            <h3 onClick={() => toggleThread(thread.id)} className="thread-header">
              ðŸ“¬ {thread.subject || '(no subject)'}
            </h3>

            {expandedThreadId === thread.id && (
              <div className="thread-body">
                {thread.messages?.map((msg, i, arr) => {
                  const showDateHeader =
                    i === 0 || new Date(msg.date).toDateString() !== new Date(arr[i - 1]?.date).toDateString();

                  return (
                    <div key={i}>
                      {showDateHeader && <div className="date-separator">{formatDateHeader(msg.date)}</div>}
                      <div
                        className={`message-bubble ${msg.isMe ? 'sent' : 'received'}`}
                        style={{ animation: 'fadeIn 0.3s ease-in' }}
                      >
                        <div className="avatar">{getInitials(msg.from)}</div>
                        <div className="message-content">
                          <div className="from-name">{msg.from}</div>
                          <div className="body-text" dangerouslySetInnerHTML={{ __html: msg.body }}></div>
                          <div className="timestamp">{new Date(msg.date).toLocaleString()}</div>
                          {msg.attachments?.length > 0 && (
                            <div className="attachments">
                              <strong>Attachments:</strong>
                              <ul>
                                {msg.attachments.map((att, index) => (
                                  <li key={index}>
                                    <a href={att.url} target="_blank" rel="noopener noreferrer">
                                      {att.filename}
                                    </a>
                                  </li>
                                ))}
                              </ul>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>ðŸ“¨ Loading emails...</p>
      )}

      {visibleCount < threads.length && (
        <button className="load-more" onClick={() => setVisibleCount(prev => prev + 5)}>
          Load More
        </button>
      )}
    </div>
  );
}
