import { useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Callback({ onLogin }) {
  const navigate = useNavigate();

  useEffect(() => {
    const handleOAuthRedirect = async () => {
      const params = new URLSearchParams(window.location.search);
      const code = params.get('code');
      if (!code) return;

      try {
        const res = await axios.get(`http://localhost:5000/api/oauth2callback?code=${code}`);
        const { tokens } = res.data;

        // Save token and notify parent component
        localStorage.setItem('accessToken', tokens.access_token);
        onLogin({ accessToken: tokens.access_token }); // or full user data if you have it

        navigate('/'); // go back to homepage
      } catch (error) {
        console.error("OAuth callback error:", error);
      }
    };

    handleOAuthRedirect();
  }, [navigate, onLogin]);

  return <p>Logging you in...</p>;
}
