import { useEffect, useState } from 'react';
import axios from 'axios';
import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import './EmailList.css';

function cleanBody(html) {
  if (!html || typeof html !== 'string') return '';
  return html
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<\/?(html|head|body|meta|title|link)[^>]*>/gi, '')
    .replace(/<div[^>]+class="gmail_quote"[^>]*>[\s\S]*$/gi, '')
    .replace(/<blockquote[^>]*>[\s\S]*?<\/blockquote>/gi, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/\s{2,}/g, ' ')
    .trim();
}

function getDateLabel(dateStr) {
  const date = new Date(dateStr);
  const today = new Date();
  const yesterday = new Date();
  yesterday.setDate(today.getDate() - 1);

  if (date.toDateString() === today.toDateString()) return 'Today';
  if (date.toDateString() === yesterday.toDateString()) return 'Yesterday';
  return date.toLocaleDateString(undefined, { year: 'numeric', month: 'short', day: 'numeric', weekday: 'long' });
}

// Simple editor factory function to create multiple editors
function createEditor() {
  return useEditor({
    extensions: [StarterKit],
    editorProps: {
      handleKeyDown(view, event) {
        if (event.key === 'Tab') {
          event.preventDefault();
          view.dispatch(
            view.state.tr.insertText('    ') // inserts 4 spaces as indent
          );
          return true;
        }
        return false;
      }
    },
    content: '',
  });
}

export default function EmailThreadManager({ token }) {
  const [profile, setProfile] = useState(null);
  const [threads, setThreads] = useState([]);
  const [search, setSearch] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [followEmails, setFollowEmails] = useState([]);
  const [emailInput, setEmailInput] = useState('');
  const [selectedThread, setSelectedThread] = useState(null);
  const [activeEmail, setActiveEmail] = useState(null);
  const [editors, setEditors] = useState({});
  const [attachments, setAttachments] = useState({});
  const [showEmailPanel, setShowEmailPanel] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/profile', {
          withCredentials: true,
        });
        setProfile(res.data);
        setUserEmail(res.data.email);
      } catch (err) {
        console.error('âŒ Failed to load profile:', err.message);
      }
    };

    fetchProfile();
  }, []);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const res = await axios.post('http://localhost:5000/api/emails/followed', {
          senders: followEmails,
        }, {
          headers: {
            'Authorization': `Bearer ${token}`
          },
          withCredentials: true,
        });
        setThreads(res.data);
      } catch (error) {
        console.error('âŒ Email fetch failed:', error.response?.data || error.message);
      }
    };

    if (followEmails.length > 0) {
      fetchEmails();
    }
  }, [followEmails, token]);
  
  const handleAddEmail = () => {
    if (emailInput && !followEmails.includes(emailInput)) {
      setFollowEmails((prev) => [...prev, emailInput]);
      setEmailInput('');
    }
  };

  const selectThread = (threadId) => {
    if (selectedThread === threadId) {
      setSelectedThread(null);
    } else {
      setSelectedThread(threadId);
      
      // Create editor for this thread if it doesn't exist
      if (!editors[threadId]) {
        const newEditor = createEditor();
        setEditors(prev => ({...prev, [threadId]: newEditor}));
      }
      
      // Initialize attachments if needed
      if (!attachments[threadId]) {
        setAttachments(prev => ({...prev, [threadId]: []}));
      }
    }
  };

  const selectEmailSender = (email) => {
    setActiveEmail(email);
    setShowEmailPanel(true);
  };

  const getInitials = (from) => {
    const name = from.split('<')[0].trim();
    return name.split(' ').map(word => word[0]).join('').slice(0, 2).toUpperCase();
  };
  
  const appendLocalReply = (threadId, message) => {
    setThreads((prev) =>
      prev.map((t) => {
        if (t.id !== threadId) return t;
        return {
          ...t,
          messages: [...t.messages, message],
        };
      })
    );
  };

  const sendReply = async (threadId, to, subject) => {
    const editor = editors[threadId];
    if (!editor) return;
    
    const html = editor.getHTML();
    const threadAttachments = attachments[threadId] || [];
    
    try {
      await axios.post('http://localhost:5000/api/reply', {
        threadId,
        to,
        subject,
        message: html,
        draft: false
      }, { withCredentials: true });

      appendLocalReply(threadId, {
        from: userEmail,
        subject,
        date: new Date().toISOString(),
        body: html,
      });

      alert('âœ… Reply sent!');
      editor.commands.setContent('');
      
      // Clear attachments for this thread
      setAttachments(prev => ({
        ...prev,
        [threadId]: []
      }));
      
    } catch (err) {
      console.error('âŒ Reply failed:', err);
      alert('Failed to send reply.');
    }
  };
  
  const saveDraft = async (threadId, to, subject) => {
    const editor = editors[threadId];
    if (!editor) return;
    
    const html = editor.getHTML();
    
    try {
      await axios.post('http://localhost:5000/api/reply', {
        threadId,
        to,
        subject,
        message: html,
        draft: true
      }, { withCredentials: true });

      alert('âœ… Draft saved!');
      
    } catch (err) {
      console.error('âŒ Draft save failed:', err);
      alert('Failed to save draft.');
    }
  };

  const handleFileAttachment = (threadId, files) => {
    setAttachments(prev => ({
      ...prev,
      [threadId]: [...(prev[threadId] || []), ...Array.from(files)]
    }));
  };
  
  const removeAttachment = (threadId, index) => {
    setAttachments(prev => {
      const newAttachments = [...prev[threadId]];
      newAttachments.splice(index, 1);
      return { ...prev, [threadId]: newAttachments };
    });
  };

  // Filter threads based on search and active email
  const filteredThreads = threads
    .filter(thread => 
      !activeEmail || thread.messages.some(msg => msg.from.includes(activeEmail))
    )
    .filter(thread =>
      !search || 
      thread.subject?.toLowerCase().includes(search.toLowerCase()) ||
      thread.messages?.some(msg =>
        msg.from?.toLowerCase().includes(search.toLowerCase()) ||
        msg.body?.toLowerCase().includes(search.toLowerCase())
      )
    );

  return (
    <div className="email-thread-manager" style={{ 
      padding: '20px', 
      fontFamily: 'Arial, sans-serif',
      maxWidth: '1200px',
      margin: '0 auto'
    }}>
      {profile && (
        <div style={{
          display: 'flex', 
          alignItems: 'center', 
          marginBottom: '20px', 
          padding: '10px',
          backgroundColor: '#f0f0f0', 
          borderRadius: '8px'
        }}>
          <img src={profile.picture} alt="avatar" style={{ borderRadius: '50%', width: 40, height: 40, marginRight: 12 }} />
          <div>
            <div><strong>{profile.name}</strong></div>
            <div style={{ fontSize: '0.9em', color: '#666' }}>{profile.email}</div>
          </div>
        </div>
      )}

      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="ðŸ” Search all threads..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: '10px', width: '100%', marginBottom: '10px', borderRadius: '8px', border: '1px solid #ccc' }}
        />

        <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
          <input
            type="text"
            placeholder="Add email to follow..."
            value={emailInput}
            onChange={(e) => setEmailInput(e.target.value)}
            style={{ padding: '10px', flex: 1, borderRadius: '8px', border: '1px solid #ccc' }}
          />
          <button
            onClick={handleAddEmail}
            style={{ padding: '10px', backgroundColor: '#2196f3', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
          >
            Add Email
          </button>
        </div>

        <div style={{ 
          display: 'flex', 
          flexWrap: 'wrap', 
          gap: '8px', 
          marginTop: '10px' 
        }}>
          {followEmails.map((email, index) => (
            <div key={index} style={{ 
              display: 'flex',
              alignItems: 'center'
            }}>
              <button 
                onClick={() => selectEmailSender(email)}
                style={{ 
                  display: 'flex', 
                  alignItems: 'center',
                  backgroundColor: activeEmail === email ? '#2196f3' : '#e3f2fd', 
                  color: activeEmail === email ? 'white' : 'black',
                  borderRadius: '20px', 
                  padding: '8px 15px',
                  border: '1px solid #bbdefb',
                  cursor: 'pointer',
                  fontWeight: activeEmail === email ? 'bold' : 'normal'
                }}
              >
                ðŸ“§ {email}
              </button>
              <button 
                onClick={() => setFollowEmails(prev => prev.filter((_, i) => i !== index))}
                style={{ 
                  marginLeft: '5px', 
                  background: 'none', 
                  border: 'none', 
                  cursor: 'pointer',
                  color: '#f44336',
                  fontWeight: 'bold',
                  fontSize: '16px'
                }}
              >
                Ã—
              </button>
            </div>
          ))}
          <button
            onClick={() => {
              setActiveEmail(null);
              setShowEmailPanel(true);
            }}
            style={{ 
              display: 'flex', 
              alignItems: 'center',
              backgroundColor: activeEmail === null && showEmailPanel ? '#2196f3' : '#e0e0e0', 
              color: activeEmail === null && showEmailPanel ? 'white' : 'black',
              borderRadius: '20px', 
              padding: '8px 15px',
              border: 'none',
              cursor: 'pointer',
              fontWeight: activeEmail === null && showEmailPanel ? 'bold' : 'normal'
            }}
          >
            ðŸ“§ All Emails
          </button>
          <button
            onClick={() => setShowEmailPanel(!showEmailPanel)}
            style={{ 
              backgroundColor: showEmailPanel ? '#f44336' : '#4caf50', 
              color: 'white',
              borderRadius: '20px', 
              padding: '8px 15px',
              border: 'none',
              cursor: 'pointer',
              marginLeft: 'auto'
            }}
          >
            {showEmailPanel ? 'âœ• Close Email Panel' : 'âœ“ Open Email Panel'}
          </button>
        </div>
      </div>

      {showEmailPanel && (
        <div style={{ display: 'flex', gap: '20px' }}>
          {/* Threads List */}
          <div style={{ 
            width: '300px', 
            borderRight: '1px solid #e0e0e0',
            padding: '0 15px 15px 0',
            overflowY: 'auto',
            maxHeight: '80vh'
          }}>
            <h3>Email Threads {activeEmail ? `from ${activeEmail}` : ''}</h3>
            
            {filteredThreads.length > 0 ? (
              <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                {filteredThreads.map((thread) => (
                  <div 
                    key={thread.id}
                    onClick={() => selectThread(thread.id)}
                    style={{
                      padding: '10px',
                      borderRadius: '8px',
                      border: selectedThread === thread.id ? '2px solid #2196f3' : '1px solid #ddd',
                      backgroundColor: selectedThread === thread.id ? '#e3f2fd' : '#f5f5f5',
                      cursor: 'pointer'
                    }}
                  >
                    <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>
                      {thread.subject || '(no subject)'}
                    </div>
                    <div style={{ fontSize: '0.8em', color: '#666' }}>
                      From: {thread.messages[0].from.split('<')[0].trim()}
                    </div>
                    <div style={{ fontSize: '0.8em', color: '#666' }}>
                      {thread.messages.length} message{thread.messages.length !== 1 ? 's' : ''} â€¢ 
                      Last activity: {new Date(thread.messages[thread.messages.length - 1].date).toLocaleString()}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div style={{ textAlign: 'center', padding: '20px', color: '#666' }}>
                {followEmails.length > 0 ? 
                  'No matching threads found.' : 
                  'Add an email address to follow.'}
              </div>
            )}
          </div>
          
          {/* Thread Content */}
          <div style={{ flex: 1 }}>
            {selectedThread ? (
              (() => {
                const thread = threads.find(t => t.id === selectedThread);
                if (!thread) return <div>Thread not found</div>;
                
                const editor = editors[selectedThread];
                const threadAttachments = attachments[selectedThread] || [];
                let lastDateLabel = '';
                
                return (
                  <div style={{ 
                    border: '1px solid #e0e0e0',
                    borderRadius: '8px',
                    padding: '15px'
                  }}>
                    <h2 style={{ borderBottom: '1px solid #e0e0e0', paddingBottom: '10px' }}>
                      {thread.subject || '(no subject)'}
                    </h2>
                    
                    <div style={{ 
                      maxHeight: '400px', 
                      overflowY: 'auto', 
                      padding: '10px',
                      marginBottom: '20px',
                      border: '1px solid #f0f0f0',
                      borderRadius: '8px'
                    }}>
                      {thread.messages.map((msg, i) => {
                        const dateLabel = getDateLabel(msg.date);
                        const showDateLabel = dateLabel !== lastDateLabel;
                        lastDateLabel = dateLabel;
                        
                        const isMe = msg.from.includes(userEmail);
                        
                        return (
                          <div key={i} style={{ display: 'flex', flexDirection: 'column', marginBottom: '15px' }}>
                            {showDateLabel && (
                              <div style={{
                                textAlign: 'center', 
                                margin: '15px 0 5px', 
                                color: '#666', 
                                fontSize: '0.85em', 
                                fontWeight: 'bold'
                              }}>
                                â”€â”€ {dateLabel} â”€â”€
                              </div>
                            )}
                            <div style={{
                              display: 'flex', 
                              flexDirection: isMe ? 'row-reverse' : 'row', 
                              alignSelf: isMe ? 'flex-end' : 'flex-start',
                              maxWidth: '75%', 
                              backgroundColor: isMe ? '#dcf8c6' : '#fff', 
                              padding: '10px', 
                              borderRadius: '10px',
                              margin: '5px 0', 
                              border: isMe ? '1px solid #a2d5a2' : '1px solid #ccc',
                              boxShadow: '0 1px 3px rgba(0,0,0,0.05)', 
                              textAlign: isMe ? 'right' : 'left'
                            }}>
                              <div style={{
                                backgroundColor: isMe ? '#4caf50' : '#888', 
                                color: '#fff', 
                                borderRadius: '50%',
                                width: '36px', 
                                height: '36px', 
                                display: 'flex', 
                                alignItems: 'center', 
                                justifyContent: 'center',
                                fontWeight: 'bold', 
                                fontSize: '0.9em', 
                                margin: isMe ? '0 0 0 10px' : '0 10px 0 0'
                              }}>
                                {isMe ? 'You' : getInitials(msg.from)}
                              </div>
                              <div style={{ flex: 1 }}>
                                <div style={{ fontWeight: 'bold' }}>{isMe ? 'You' : msg.from}</div>
                                <div style={{ marginTop: '6px', marginBottom: '8px' }}
                                  dangerouslySetInnerHTML={{ __html: cleanBody(msg.body) }} />
                                <div style={{ fontSize: '0.8em', color: '#888' }}>{new Date(msg.date).toLocaleString()}</div>
                              </div>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                    
                    <div style={{ 
                      border: '1px solid #e0e0e0',
                      borderRadius: '8px',
                      padding: '15px'
                    }}>
                      <h3 style={{ marginTop: 0 }}>Reply</h3>
                      
                      {editor && (
                        <div style={{ marginBottom: '10px', display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
                          <button
                            onClick={() => editor.chain().focus().toggleBold().run()}
                            style={{ 
                              fontWeight: 'bold', 
                              padding: '5px 10px',
                              borderRadius: '4px',
                              border: '1px solid #ccc',
                              backgroundColor: editor.isActive('bold') ? '#e3f2fd' : '#fff'
                            }}
                          >B</button>
                          <button 
                            onClick={() => editor.chain().focus().toggleItalic().run()} 
                            style={{ 
                              fontStyle: 'italic',
                              padding: '5px 10px',
                              borderRadius: '4px',
                              border: '1px solid #ccc',
                              backgroundColor: editor.isActive('italic') ? '#e3f2fd' : '#fff'
                            }}
                          >I</button>
                          <button 
                            onClick={() => editor.chain().focus().toggleBulletList().run()}
                            style={{ 
                              padding: '5px 10px',
                              borderRadius: '4px',
                              border: '1px solid #ccc',
                              backgroundColor: editor.isActive('bulletList') ? '#e3f2fd' : '#fff'
                            }}
                          >â€¢ List</button>
                        </div>
                      )}
                      
                      {editor && (
                        <EditorContent 
                          editor={editor} 
                          className="tiptap-editor"
                          style={{
                            border: '1px solid #ccc',
                            borderRadius: '6px',
                            minHeight: '150px',
                            padding: '10px',
                            marginBottom: '10px'
                          }}
                        />
                      )}
                      
                      <div style={{ margin: '10px 0' }}>
                        <label style={{ fontSize: '0.9em', fontWeight: 'bold' }}>ðŸ“Ž Attach files:
                          <input 
                            type="file" 
                            multiple
                            onChange={(e) => handleFileAttachment(selectedThread, e.target.files)} 
                            style={{ display: 'block', marginTop: '6px' }} 
                          />
                        </label>
                        <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '10px', gap: '10px' }}>
                          {threadAttachments.map((file, index) => (
                            <div key={index} style={{ 
                              border: '1px solid #ccc', 
                              borderRadius: '4px', 
                              padding: '4px 8px', 
                              background: '#f0f0f0',
                              display: 'flex',
                              alignItems: 'center'
                            }}>
                              <span style={{ marginRight: '5px' }}>ðŸ“„</span>
                              {file.name}
                              <button 
                                onClick={() => removeAttachment(selectedThread, index)} 
                                style={{ 
                                  marginLeft: '8px', 
                                  color: 'red', 
                                  background: 'none', 
                                  border: 'none', 
                                  cursor: 'pointer',
                                  fontSize: '16px',
                                  fontWeight: 'bold'
                                }}
                              >Ã—</button>
                            </div>
                          ))}
                        </div>
                      </div>
                      
                      <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
                        <button
                          onClick={() => sendReply(selectedThread, thread.messages[0].from, thread.subject)}
                          style={{ 
                            padding: '8px 16px', 
                            backgroundColor: '#4caf50', 
                            color: '#fff', 
                            border: 'none', 
                            borderRadius: '6px', 
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '5px'
                          }}
                        >
                          <span style={{ fontSize: '1.2em' }}>âœ‰ï¸</span>
                          Send Reply
                        </button>
                        <button
                          onClick={() => saveDraft(selectedThread, thread.messages[0].from, thread.subject)}
                          style={{ 
                            padding: '8px 16px', 
                            backgroundColor: '#ff9800', 
                            color: '#fff', 
                            border: 'none', 
                            borderRadius: '6px', 
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '5px'
                          }}
                        >
                          <span style={{ fontSize: '1.2em' }}>ðŸ’¾</span>
                          Save Draft
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })()
            ) : (
              <div style={{ 
                textAlign: 'center', 
                padding: '40px', 
                backgroundColor: '#f5f5f5', 
                borderRadius: '10px',
                border: '1px dashed #ccc'
              }}>
                <h3>Select a thread to view</h3>
                <p>Click on any email thread from the list to view its contents and reply.</p>
              </div>
            )}
          </div>
        </div>
      )}
      
      {!showEmailPanel && (
        <div style={{ 
          textAlign: 'center', 
          padding: '40px', 
          backgroundColor: '#f5f5f5', 
          borderRadius: '10px',
          border: '1px dashed #ccc'
        }}>
          {followEmails.length > 0 ? (
            <>
              <h3>Your Email Interface</h3>
              <p>Click the "Open Email Panel" button above to view and manage your emails.</p>
            </>
          ) : (
            <>
              <h3>No Emails Configured</h3>
              <p>Add an email address to follow above, then open the email panel.</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}
