import { useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Callback({ onLogin }) {
  const navigate = useNavigate();

  useEffect(() => {
    const code = new URLSearchParams(window.location.search).get('code');
    console.log("ðŸ” Code param:", code);

    const handleOAuthRedirect = async () => {
      if (!code) {
        console.error('No authorization code received');
        return;
      }

      try {
        const res = await axios.get(`http://localhost:5000/api/oauth2callback?code=${code}`);
        const accessToken = res.data.tokens?.access_token;

        if (accessToken) {
          console.log('âœ… Access Token:', accessToken);
          localStorage.setItem('accessToken', accessToken);
          onLogin({ accessToken }); // Pass the token to the parent component
          navigate('/'); // Redirect to home or email list page
        } else {
          throw new Error('Access token is missing');
        }
      } catch (error) {
        console.error('OAuth callback failed:', error);
        localStorage.removeItem('accessToken'); // Clean up any invalid token
        navigate('/'); // Optionally redirect to login page if failure
      }
    };

    handleOAuthRedirect();
  }, [code, navigate, onLogin]);

  return <p>Logging you in...</p>;
}
