import { useEffect, useState } from 'react';
import axios from 'axios';
import './EmailList.css'; // Optional: externalize CSS for animation

function cleanBody(html) {
  return html
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<\/?(html|head|body|meta|title|link)[^>]*>/gi, '')
    .replace(/<div[^>]+class="gmail_quote"[^>]*>[\s\S]*$/gi, '') // Gmail reply thread
    .replace(/<blockquote[^>]*>[\s\S]*?<\/blockquote>/gi, '')     // Outlook/Gmail blockquotes
    .replace(/&nbsp;/g, ' ')
    .replace(/\s{2,}/g, ' ')
    .trim();
}


export default function EmailList({ token }) {
  const [threads, setThreads] = useState([]);
  const [expandedThreadId, setExpandedThreadId] = useState(null);
  const [search, setSearch] = useState('');
  const [visibleCount, setVisibleCount] = useState(5);

  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/emails', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setThreads(res.data);
      } catch (error) {
        if (error.response?.status === 401) {
          localStorage.removeItem('accessToken');
          window.location.href = '/'; // Force re-auth
        } else {
          console.error('Email fetch failed:', error.response?.data || error.message);
        }
      }
    };

    if (token) fetchEmails();
  }, [token]);

  const toggleThread = (id) => {
    setExpandedThreadId(expandedThreadId === id ? null : id);
  };

  const getInitials = (from) => {
    const name = from.split('<')[0].trim();
    return name.split(' ').map(word => word[0]).join('').slice(0, 2).toUpperCase();
  };

  const filteredThreads = threads
    .filter((thread) => thread.subject?.toLowerCase().includes(search.toLowerCase()) ||
      thread.messages?.some(msg => msg.from?.toLowerCase().includes(search.toLowerCase()) ||
        msg.body?.toLowerCase().includes(search.toLowerCase())))
    .slice(0, visibleCount);

  return (
    <div className="email-list" style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <input
        type="text"
        placeholder="🔍 Search emails..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: '10px', width: '100%', marginBottom: '20px', borderRadius: '8px', border: '1px solid #ccc' }}
      />

      {filteredThreads.length > 0 ? (
        filteredThreads.map((thread) => (
          <div
            key={thread.id}
            className={`email-thread ${expandedThreadId === thread.id ? 'expanded' : ''}`}
            style={{
              border: '2px solid #ccc',
              borderRadius: '10px',
              padding: '15px',
              marginBottom: '25px',
              backgroundColor: '#fafafa',
              boxShadow: '0 2px 6px rgba(0,0,0,0.05)',
              transition: 'all 0.3s ease',
            }}
          >
            <h3
              style={{ cursor: 'pointer', color: '#333' }}
              onClick={() => toggleThread(thread.id)}
            >
              📬 {thread.subject || '(no subject)'}
            </h3>

            {expandedThreadId === thread.id && (
              <div style={{ maxHeight: '400px', overflowY: 'auto', paddingRight: '10px' }}>
                {thread.messages?.map((msg, i) => (
                  <div
                    key={i}
                    style={{
                      display: 'flex',
                      alignItems: 'flex-start',
                      gap: '10px',
                      padding: '10px 14px',
                      margin: '10px 0',
                      borderRadius: '8px',
                      backgroundColor: msg.isMe ? '#e1fbe1' : '#eaeaea',
                      borderLeft: msg.isMe ? '4px solid #4caf50' : '4px solid #888',
                      animation: 'fadeIn 0.3s ease-in',
                    }}
                  >
                    <div
                      style={{
                        backgroundColor: '#888',
                        color: '#fff',
                        borderRadius: '50%',
                        width: '36px',
                        height: '36px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        fontWeight: 'bold',
                        fontSize: '0.9em',
                      }}
                    >
                      {getInitials(msg.from)}
                    </div>
                    <div style={{ flex: 1 }}>
                      <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>{msg.from}</div>
                      <div
                        className="email-body"
                        style={{ whiteSpace: 'normal', marginBottom: '6px' }}
                        dangerouslySetInnerHTML={{ __html: cleanBody(msg.body) }}
                      ></div>

                      <div style={{ fontSize: '0.85em', color: '#666' }}>
                        {new Date(msg.date).toLocaleString()}
                      </div>
                      {msg.attachments?.length > 0 && (
                        <div style={{ marginTop: '8px' }}>
                          <strong>Attachments:</strong>
                          <ul style={{ paddingLeft: '18px' }}>
                            {msg.attachments.map((att, index) => (
                              <li key={index}>
                                <a href={att.url} target="_blank" rel="noopener noreferrer">
                                  {att.filename}
                                </a>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>📨 Loading emails...</p>
      )}

      {visibleCount < threads.length && (
        <button
          onClick={() => setVisibleCount(prev => prev + 5)}
          style={{
            marginTop: '15px',
            padding: '10px 18px',
            borderRadius: '8px',
            backgroundColor: '#4caf50',
            color: '#fff',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Load More
        </button>
      )}
    </div>
  );
}
